import React from 'react';
// import DisplayGrid from './DisplayGrid/DisplayGrid';
import axios from 'axios';
//import {  Container, Col } from 'react-bootstrap';
import { Grid, Button, CircularProgress } from '@material-ui/core';
import { ButtonWrapper, Item } from './style';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      URLs: [], 
      loading: false
    }
    this.handleUrlsClick = this.handleUrlsClick.bind(this)
  } 

  // switchDogs = () => {
  //   axios.get('https://random.dog/woof.json')
  //   .then(response => {
  //     this.setState({ URLs: response.data.url});
  //   })
  //   .catch(error => {
  //     console.log(error);
  //   });
  // }



  handleUrlsClick = async () => {
    try {
      let i = 0;
      let urllist = []
      for(i;i< 8;i++){
        const response = await axios.get(`https://random.dog/woof.json`)
        const json = await response.data
        //console.log(json.url)
        urllist.push(json.url)
      }
      this.setState({ URLs: urllist, loading: true });
      setTimeout(() => this.setState({ loading: false }), 3000);
      console.log({urllist})
    } catch (error) {
        console.log(error.response.body);
    }
  }

  render() {
    const { URLs, loading } = this.state;
    return (
      // <div className="App">
      //   <button onClick={this.videoUrls}>Switch Dogs</button>
      //   <p>{URLs}</p>
      //   <DisplayGrid imageURL={URLs[0]} /> 
      //   <DisplayGrid imageURL={URLs[1]} /> 
      //   <DisplayGrid imageURL={URLs[2]} /> 
      //   <DisplayGrid imageURL={URLs[3]} /> 
      // </div>
      <>
      <ButtonWrapper>
        <Button variant="outlined" color="secondary" onClick={this.handleUrlsClick} disabled={loading}>

          {!loading && 'Show Dogs'}
          {loading && <CircularProgress size={14} />}
        </Button>
      </ButtonWrapper>
      {/* <button onClick={this.handleUrlsClick}>Switch Dogs</button> */}
      {/* <div>
        {
            URLs.map((item, index)=> {
                return (
                    <DisplayGrid key={index} imageURL={item} />
                )
            })
        }

      </div>   */}

     <div>
        <Grid container>
          {URLs.map((item, index) => (
            <Grid item xs={12} md={3} key={index}>
              <Item>
                <img className='pic' src={item} alt='' />
              </Item>
            </Grid>
          ))}
          </Grid>
			</div>
    </>
    );
  }
}

export default App;
